sequence.script:

				var email = mc.getProperty('uri.var.email');
				var unsubscribed = mc.getProperty('uri.var.unsubscribed');

                var query="";
                var added=0;
                if (email != null && email != ""){
                    var ids=email.split(',');
                    for(var i = 0; i < ids.length; i++){
                        query+='id='+ids[i]+'&amp;';
                    }
                }

                if (unsubscribed != null && unsubscribed != ""){
                    var nameLists=unsubscribed.split(',');
                    for(var i = 0; i < nameLists.length; i++){
                        var name=nameLists[i];
                        query+='name='+name+'&amp;';
                    }
                }
